#
# TABLE STRUCTURE FOR: game
#

DROP TABLE IF EXISTS `game`;

CREATE TABLE `game` (
  `game_id` int(11) NOT NULL,
  `additional_content_id` int(11) NOT NULL,
  `news_id` int(11) NOT NULL,
  `publicshing_house_idpublicshing_house` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



#
# TABLE STRUCTURE FOR: game_additional_content
#

DROP TABLE IF EXISTS `game_additional_content`;

CREATE TABLE `game_additional_content` (
  `idgame_additional_content` int(11) NOT NULL,
  `content` blob DEFAULT NULL,
  PRIMARY KEY (`idgame_additional_content`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



#
# TABLE STRUCTURE FOR: game_and_tags
#

DROP TABLE IF EXISTS `game_and_tags`;

CREATE TABLE `game_and_tags` (
  `game_id` int(11) NOT NULL,
  `game_tag_id` int(11) NOT NULL,
  PRIMARY KEY (`game_id`,`game_tag_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



#
# TABLE STRUCTURE FOR: game_news
#

DROP TABLE IF EXISTS `game_news`;

CREATE TABLE `game_news` (
  `idgame_new` int(11) NOT NULL,
  `new` mediumtext COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`idgame_new`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



#
# TABLE STRUCTURE FOR: game_tag
#

DROP TABLE IF EXISTS `game_tag`;

CREATE TABLE `game_tag` (
  `idgame_tag` int(11) NOT NULL,
  `tag_name` varchar(45) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`idgame_tag`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



